// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { MessagingFee, Origin } from "@layerzerolabs/oapp-evm/contracts/oapp/OApp.sol";
import { ILayerZeroEndpointV2, MessagingParams, MessagingReceipt } from "@layerzerolabs/lz-evm-protocol-v2/contracts/interfaces/ILayerZeroEndpointV2.sol";
import { OwnableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { Initializable } from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title BaseLayerZeroUpgradeable
 * @dev Base contract for upgradeable LayerZero applications
 */
abstract contract BaseLayerZeroUpgradeable is 
    Initializable,
    OwnableUpgradeable,
    UUPSUpgradeable 
{
    /* -------------------------------------------------------------------------- */
    /*                                   STATE                                    */
    /* -------------------------------------------------------------------------- */
    ILayerZeroEndpointV2 public endpoint;
    mapping(uint32 => bytes32) public peers;

    /* -------------------------------------------------------------------------- */
    /*                                   EVENTS                                   */
    /* -------------------------------------------------------------------------- */
    event PeerSet(uint32 indexed eid, bytes32 peer);

    /* -------------------------------------------------------------------------- */
    /*                                 ERRORS                                     */
    /* -------------------------------------------------------------------------- */
    error OnlyEndpoint();
    error NoPeer(uint32 eid);
    error InvalidPeer(bytes32 peer);

    /* -------------------------------------------------------------------------- */
    /*                               INITIALIZER                                  */
    /* -------------------------------------------------------------------------- */
    function __BaseLayerZero_init(
        address _endpoint,
        address _owner
    ) internal onlyInitializing {
        __Ownable_init(_owner);
        __UUPSUpgradeable_init();
        endpoint = ILayerZeroEndpointV2(_endpoint);
    }

    /* -------------------------------------------------------------------------- */
    /*                              PEER MANAGEMENT                               */
    /* -------------------------------------------------------------------------- */
    function setPeer(uint32 _eid, bytes32 _peer) external onlyOwner {
        if (_peer == bytes32(0)) revert InvalidPeer(_peer);
        peers[_eid] = _peer;
        emit PeerSet(_eid, _peer);
    }

    /* -------------------------------------------------------------------------- */
    /*                        LAYERZERO HELPER FUNCTIONS                         */
    /* -------------------------------------------------------------------------- */
    function _quote(
        uint32 _dstEid,
        bytes memory _message,
        bytes memory _options,
        bool _payInLzToken
    ) internal view returns (MessagingFee memory) {
        return endpoint.quote(
            MessagingParams({
                dstEid: _dstEid,
                receiver: peers[_dstEid],
                message: _message,
                options: _options,
                payInLzToken: _payInLzToken
            }),
            address(this)
        );
    }

    function _lzSend(
        uint32 _dstEid,
        bytes memory _message,
        bytes memory _options,
        MessagingFee memory _fee,
        address payable _refundTo
    ) internal returns (MessagingReceipt memory) {
        return endpoint.send{value: _fee.nativeFee}(
            MessagingParams({
                dstEid: _dstEid,
                receiver: peers[_dstEid],
                message: _message,
                options: _options,
                payInLzToken: _fee.lzTokenFee > 0
            }),
            _refundTo
        );
    }

    /* -------------------------------------------------------------------------- */
    /*                        LAYERZERO RECEIVE HOOK                             */
    /* -------------------------------------------------------------------------- */
    function lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata _message,
        address _executor,
        bytes calldata _extraData
    ) external payable {
        if (msg.sender != address(endpoint)) revert OnlyEndpoint();
        if (_origin.sender != peers[_origin.srcEid]) revert InvalidPeer(_origin.sender);

        _lzReceive(_origin, _guid, _message, _executor, _extraData);
    }

    function _lzReceive(
        Origin calldata _origin,
        bytes32 _guid,
        bytes calldata _message,
        address _executor,
        bytes calldata _extraData
    ) internal virtual;

    /* -------------------------------------------------------------------------- */
    /*                             UPGRADE AUTHORIZATION                           */
    /* -------------------------------------------------------------------------- */
    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /* -------------------------------------------------------------------------- */
    /*                               STORAGE GAP                                  */
    /* -------------------------------------------------------------------------- */
    uint256[48] private __gap;
}

